package heroes.battle;public class Cell {    private final int x;    private boolean isBusy = false;    protected Cell(int x) {        this.x = x;    }    public int getX() {        return x;    }    public boolean isBusy() {        return isBusy;    }    public void setBusy() {        isBusy = true;    }    public void setFree(){        isBusy = false;    }}